import React, { useState } from "react";
import {Login}   from "../../services/Login.service"
import { Link,Redirect } from "react-router-dom";
import {SetToken} from "../../services/Session.service"

const initialState = {
    email: "",
    password: "",
    isLoading: false
}


export default function LoginComponent(){
    const [state, setState] = new useState(initialState);
    function onChangeEmail(event){
        setState((prevState) => ({...prevState, email: event.target.value}))
    }
    function onChangePassword(event){
        setState((prevState) => ({...prevState, password: event.target.value}))
    }
    async function handleLoginSubmit(){
        const result = await Login(state.email,state.password)
        if(result != null){
            SetToken(result)
            return <redi to="/profile"/>
        }
    }
    return (
        <div className="column">
                <h1 className="title has-text-centered">{"Factored"}</h1>
                <div className="field">
                    <label className="label">Email</label>
                    <div className="control">
                        <input type="email" placeholder="Enter email" value={state.email} onChange={onChangeEmail} className="input" required/>
                    </div>
                </div>
                <div className="field">
                    <label className="label">Password</label>
                    <div className="control">
                        <input type="password" placeholder="Enter password" value={state.password} onChange={onChangePassword} className="input" required/>
                    </div>
                </div>
                <div className="field is-grouped is-grouped-centered">
                    <div className="control">
                        <button type="button" className="button is-primary" onClick = {handleLoginSubmit}>{"Login"}</button>
                    </div>
                </div>
                <div className="field is-grouped is-grouped-centered">
                    <div className="control">
                        <Link type="button" className="button is-light" to={"/register"}>Dont you have a account? Register here</Link>
                    </div>
                </div>
        </div>
    );
}
